import './Product.css'
import React, {Component} from "react";

const products = [
    {
    emoji: 
   'ðŸ¦',
    name: 
   'ice cream',
    price: 5
    },
    {
    emoji: 
   'ðŸ©',
    name: 
   'donuts',
    price: 
   2.5,
    },
    {
    emoji: 
   'ðŸ‰',
    name: 
   'watermelon',
    price: 4
    }
    ];

export default class Product extends Component { 
    state = { 
        cart: [ ], 
        total: 0 
    }

    // mengatur options formatter pada currency 
    // properti nya tidak boleh typo
    currenceyOptions = { 
        minimumFractionDigits: 2, 
        maximumFractionDigits:2 , 


    }

    getTotal = ()=> { 
        return this.state.total.toLocaleString(undefined, this.currenceyOptions) ; 
    }

    remove =() => {
        this.setState( { 
            card: [''], 
            total: 0 , 
        })
    }

    add = ()=> { 
        this.setState ({ 
            card: ['ice_cream'], 
            total: 5 , 
        })

    }
    render() { 
        return ( 
            <div className="wrapper"> 
            <div>Shopping Cart: {this.state.cart.length} total items </div>
            <div>
                Total: {this.getTotal()} </div>

            <div> 
                {products.map(product=> { 
                    <div key={product.name}>
                        <span>{product.name}</span></div>
                })}




                <button onClick={this.add}> add </button>  <button onClick={this.remove}>remove</button>
                </div>
                </div>



        )
    }
}
